function onSuccess(e){var t=$("#random-posts-list"),l=shuffleArray(e.posts),a=l.slice(0,3);$.each(a,function(e,l){t.append('<li class="collection-item"><a href="'+l.url+'">'+l.title+"</a></li>")})}function shuffleArray(e){for(var t=e.length-1;t>0;t--){var l=Math.floor(Math.random()*(t+1)),a=e[t];e[t]=e[l],e[l]=a}return e}$(document).ready(function(){$.get(ghost.url.api("posts",{limit:"all",filter:"featured:false+image:-null"})).done(onSuccess)});
